	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(StateSearchPeer::DATABASE_NAME);

		if ($this->isColumnModified(StateSearchPeer::STATEID)) $criteria->add(StateSearchPeer::STATEID, $this->stateid);
		if ($this->isColumnModified(StateSearchPeer::COUNTRYNAME)) $criteria->add(StateSearchPeer::COUNTRYNAME, $this->countryname);
		if ($this->isColumnModified(StateSearchPeer::STATENAME)) $criteria->add(StateSearchPeer::STATENAME, $this->statename);
		if ($this->isColumnModified(StateSearchPeer::STATECODE)) $criteria->add(StateSearchPeer::STATECODE, $this->statecode);
		if ($this->isColumnModified(StateSearchPeer::ABREVIATION)) $criteria->add(StateSearchPeer::ABREVIATION, $this->abreviation);
		if ($this->isColumnModified(StateSearchPeer::ABREVIATEDSTATENAME)) $criteria->add(StateSearchPeer::ABREVIATEDSTATENAME, $this->abreviatedstatename);
		if ($this->isColumnModified(StateSearchPeer::BROKERSTATENAME)) $criteria->add(StateSearchPeer::BROKERSTATENAME, $this->brokerstatename);
		if ($this->isColumnModified(StateSearchPeer::PROJECTSTATE)) $criteria->add(StateSearchPeer::PROJECTSTATE, $this->projectstate);
		if ($this->isColumnModified(StateSearchPeer::RETAILBROKERSTATE)) $criteria->add(StateSearchPeer::RETAILBROKERSTATE, $this->retailbrokerstate);
		if ($this->isColumnModified(StateSearchPeer::PROJECTCODE)) $criteria->add(StateSearchPeer::PROJECTCODE, $this->projectcode);
		if ($this->isColumnModified(StateSearchPeer::CREATEDBY)) $criteria->add(StateSearchPeer::CREATEDBY, $this->createdby);
		if ($this->isColumnModified(StateSearchPeer::CREATEDON)) $criteria->add(StateSearchPeer::CREATEDON, $this->createdon);
		if ($this->isColumnModified(StateSearchPeer::MODIFIEDBY)) $criteria->add(StateSearchPeer::MODIFIEDBY, $this->modifiedby);
		if ($this->isColumnModified(StateSearchPeer::MODIFIEDON)) $criteria->add(StateSearchPeer::MODIFIEDON, $this->modifiedon);
		if ($this->isColumnModified(StateSearchPeer::ID)) $criteria->add(StateSearchPeer::ID, $this->id);

		return $criteria;
	}

