	 */
	public static function buildTableMap()
	{
	  $dbMap = Propel::getDatabaseMap(BaseSubmissionPeer::DATABASE_NAME);
	  if (!$dbMap->hasTable(BaseSubmissionPeer::TABLE_NAME))
	  {
